const style = StyleSheet.create({
    
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginVertical: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginVertical: 10,
  },
  textInput: {
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 5,
    marginVertical: 10,
    width: '100%',
  },
  button: {
    marginVertical: 10,
  },
  text: {
    color: 'white',
  },
  genderButton: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 5,
  },
  radioButtonIcon: {
    width: 20,
    height: 20,
    borderRadius: 20 / 2,
    borderWidth: 2,
    borderColor: '#4c4c4c',
    marginRight: 10,
  },
});
export default {style};
